/**
 * 模拟java集合中Map的对象，方便用于存储key-value数据，使用方法如：
 * 
 * $(function() {
 * 	$.Map.put('key', 'value');
 * 	alert($.Map.get('key'));
 * });
 * 
 * 
 * @author lianshisheng@126.com
 * @date 2015-02-03
 */
jQuery.Map={datamap:{},get:function(key){return this.datamap[key]},put:function(key,value){this.datamap[key]=value},size:function(){return this.keys().length},remove:function(key){delete this.datamap[key]},isEmpty:function(){return this.size()==0},containsKey:function(key){return key in this.datamap},containsValue:function(value){return this.values().indexOf(value)>-1},clear:function(){for(key in this.datamap){delete this.datamap[key]}},keys:function(){keys=[];for(key in this.datamap){keys.push(key)}return keys},values:function(){values=[];for(key in this.datamap){values.push(this.datamap[key])}return values}};